mapper.select:
SELECT id, user_id AS userId, label, recipient_name AS recipientName, phone, postal_code AS postalCode, address, address_detail AS addressDetail, is_default AS isDefault
FROM user_addresses
WHERE id = #{id}

mapper.update:
UPDATE user_addresses
SET label = #{label}, recipient_name = #{recipientName}, phone = #{phone}, postal_code = #{postalCode}, address = #{address}, address_detail = #{addressDetail}, is_default = #{isDefault}
WHERE id = #{id}
